import React, {Component} from 'react';
import './App.css';
import Contract from './Contract';
import Navbar from './Navbar';
import * as ReactBootStrap from "react-bootstrap";
import { BrowserRouter as Router, Routes, Route }
    from 'react-router-dom';
import Contact from './Contact';
import About from './About';
import { useLocation } from 'react-router-dom';


function App(){



  return <div>
    <Router>
    const location = useLocation();
  const hideComponent = location.pathname === '/Contact';

            <Navbar/>
            <Routes>
           <Route path='/Contract' elements={<Contract />} style={{ display: hideComponent ? 'none' : 'block' }}/>
            <Route path='/Contact' element={<Contact />} onclick="hideMain()" />
            <Route path='/About' element={<About />} />
            </Routes>
            </Router>
      <div id="mainPage">
        <Contract/>
      </div>
  </div>

function hideMain(){
  console.log("Inside hide function");
  document.getElementById("mainPage").style.display = "none";
}

}

export default App;
